export interface CreateDispute {
    DisputeData?: DisputeData;
    Documents?: DisputeDocument[];
    Attachment?: Array<Attachment>;
}

export interface DisputeData {
    disputeCategoryCode: string;
    countryCode: string;
    companyCode: string;
    divisionCode: string;
    customerCode: string;
    comment: string;
}

export interface DisputeDocument {
    documentId: string;
}

export interface Attachment {
    fileName: string;
    fileContent: string;
}
