import { PlantDTO } from './plantDTO';
import { UserDTO } from './userDTO'
import { IOrderTruck } from './drivers.order.dto';

export interface IPlantOptions {
    plantId: string;
    plantCode: string;
    plantDesc: string;
    monitoredBy: UserDTO[];
    status: boolean;
}

export class DriverPlantDto extends PlantDTO  implements IOrderTruck {

    trucks: any[] = [];

    constructor(option?: IPlantOptions) {
        super();
        this.monitoredBy = option.monitoredBy;
        this.plantCode = option.plantCode;
        this.plantDesc = option.plantDesc;
        this.plantId = option.plantId;
        this.status = option.status;

    }

    get plantDescView():string {
        return `${this.plantDesc} (${this.trucks.length})`
    }

}