describe('Start of week change', function () {
    it('starts the application', function () {
        cy.clearCookies();
        cy.clearLocalStorage();
        cy.window().then((win) => {
            win.sessionStorage.clear();
        });
        cy.visit('http://localhost:8000/');
    });
    it('Click startweek', function () {
      cy.get('[data-tid="cmx-link-datepicker-startweek"]').click({ force: true });
    });
    it('check the first day is monday', function () {
      cy.get('thead > .cmx-datepicker__days-grid-row > :nth-child(1)').click({ force: true });
      cy.contains('thead > .cmx-datepicker__days-grid-row > :nth-child(1)','Mon');
    });

});
