styles:
html {
  font-family: sans-serif;
  line-height: 1.15;
  -ms-text-size-adjust: 100%;
  -webkit-text-size-adjust: 100%;
}

body {
  margin: 0;
}

input {
  font-family: sans-serif;
  font-size: 100%;
  line-height: 1.15;
  margin: 0;
}

input {
  overflow: visible;
}

html [type="button"],[type="reset"],[type="submit"] {
  -webkit-appearance: button;
}

[type="button"]::-moz-focus-inner,[type="reset"]::-moz-focus-inner,[type="submit"]::-moz-focus-inner {
  border-style: none;
  padding: 0;
}

[type="button"]:-moz-focusring,[type="reset"]:-moz-focusring,[type="submit"]:-moz-focusring {
  outline: 1px dotted ButtonText;
}

[type="checkbox"],[type="radio"] {
  box-sizing: border-box;
  padding: 0;
}

[type="number"]::-webkit-inner-spin-button,[type="number"]::-webkit-outer-spin-button {
  height: auto;
}

[type="search"] {
  -webkit-appearance: textfield;
  outline-offset: -2px;
}

[type="search"]::-webkit-search-cancel-button,[type="search"]::-webkit-search-decoration {
  -webkit-appearance: none;
}

::-webkit-file-upload-button {
  -webkit-appearance: button;
  font: inherit;
}

[hidden] {
  display: none;
}

html {
  box-sizing: border-box;
}

*,*::before,*::after {
  box-sizing: inherit;
}

html {
  -ms-overflow-style: scrollbar;
  -webkit-tap-highlight-color: transparent;
}

body {
  font-family: -apple-system,system-ui,BlinkMacSystemFont,"Segoe UI",Roboto,"Helvetica Neue",Arial,sans-serif;
  font-size: 1rem;
  font-weight: normal;
  line-height: 1.5;
  color: #292b2c;
  background-color: #fff;
}

[tabindex="-1"]:focus {
  outline: none !important;
}

[role="button"] {
  cursor: pointer;
}

[role="button"],input,label {
  touch-action: manipulation;
}

label {
  display: inline-block;
  margin-bottom: .5rem;
}

input {
  line-height: inherit;
}

input[type="radio"]:disabled,input[type="checkbox"]:disabled {
  cursor: not-allowed;
}

input[type="date"],input[type="time"],input[type="datetime-local"],input[type="month"] {
  -webkit-appearance: listbox;
}

input[type="search"] {
  -webkit-appearance: none;
}

output {
  display: inline-block;
}

[hidden] {
  display: none !important;
}

@font-face {
  font-family: "Roboto";
  font-family: "Roboto-Thin";
  font-weight: 100;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Light.ttf");
  font-weight: 300;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Regular.ttf");
  font-weight: 400;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Italic.ttf");
  font-weight: 400;
  font-style: italic;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Medium.ttf");
  font-weight: 500;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Bold.ttf");
  font-weight: 700;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Black.ttf");
  font-weight: 800;
}

:host {
  font-family: "Roboto",sans-serif;
  font-size: 1rem;
  -moz-box-sizing: border-box;
  -webkit-box-sizing: border-box;
  box-sizing: border-box;
  position: relative;
}

:host input[type=checkbox] {
  position: absolute;
  opacity: 0;
  z-index: 1;
  width: 16px;
  height: 22px;
  margin-right: 0.375rem;
}

:host input[type=checkbox]+label {
  display: inline-flex;
  align-items: center;
  position: relative;
  height: 16px;
}

:host input[type=checkbox]+label::before {
  content: ' ';
  display: inline-block;
  width: 16px;
  height: 16px;
  box-sizing: border-box;
  border-width: 1px;
  border-color: #B0B0B0;
  border-style: solid;
  border-radius: 1px;
  margin-right: 0.375rem;
}

:host input[type=checkbox]:hover+label::before,:host input[type=checkbox]:focus+label::before {
  background-color: #E8EBEF;
}

:host input[type=checkbox]:checked+label::before {
  background-color: #3FA9F5;
  border-color: #3FA9F5;
}

:host input[type=checkbox]:checked+label::after {
  content: ' ';
  top: 4px;
  left: 3px;
  position: absolute;
  width: 10px;
  height: 6px;
  border-left: 2.5px solid #fff;
  border-bottom: 2.5px solid #fff;
  border-top: none;
  border-right: none;
  transform: rotate(-45deg);
}

:host input[type=checkbox].error+label::before {
  border-color: #EE3D42;
}

:host input[type=checkbox][disabled]+label::before {
  background-color: #D6D6D6;
}

:host[dir='rtl'] input[type=checkbox] {
  margin-left: 0.375rem;
  margin-right: 0;
}

:host[dir='rtl'] input[type=checkbox]+label::before {
  margin-left: 0.375rem;
  margin-right: 0;
}

:host[dir='rtl'] input[type=checkbox]:checked+label::after {
  content: ' ';
  top: 4px;
  right: 3px;
  left: auto;
}

:host input,:host label {
  cursor: pointer;
}

:host label.--disabled {
  cursor: not-allowed;
}

