describe('Angular Library Starter APP',function(){
  it('starts the application', function(){
    cy.clearCookies();
    cy.clearLocalStorage();
    cy.window().then((win) => {
      win.sessionStorage.clear();
    });
    cy.visit('http://localhost:8000//');
  });
  it('Button one should be checked', function(){
    cy.get('#radio1-input').should('be.checked');
  });
  it('Button two should be no checked', function(){
    cy.get('#radio2-input').should('not.checked');
  });
  it('Click botton two and botton two should be checked', function(){
    cy.get('#radio1-input').click({ force: true });
    cy.get('#radio1-input').should('be.checked');
  });
  it('First button on "Example radio with error" should not have "error" class and be should be unchecked', function(){
    cy.get('#radio7-input').should('not.checked');
    cy.get('#radio7-input').should('not.have.class', 'error');
  });
  it('First button on "Example radio with error" should change to have have "error" class and should be checked', function(){
    cy.get('#radio7-input').click({ force: true });
    cy.get('#radio7-input').should('be.checked');
    cy.get('#radio7-input').should('have.class', 'error');
  });
});

