  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Light.ttf");
  font-weight: 300;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Regular.ttf");
  font-weight: 400;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Italic.ttf");
  font-weight: 400;
  font-style: italic;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Medium.ttf");
  font-weight: 500;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Bold.ttf");
  font-weight: 700;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Black.ttf");
  font-weight: 800;
}

a {
  text-decoration: none;
  color: #3FA9F5;
}

a:hover {
  text-decoration: underline;
  color: #3FA9F5;
}

a:active {
  text-decoration: underline;
  color: #003876;
}

a.disabled {
  text-decoration: none;
  color: #666;
  cursor: not-allowed;
}

.cmx-radio {
  font-family: "Roboto",sans-serif;
  font-weight: 500;
}

.cmx-radio input[type=radio] {
  position: absolute;
  opacity: 0;
  z-index: 1;
  width: 16px;
  height: 22px;
  margin-right: 0.375rem;
}

.cmx-radio input[type=radio]+label {
  display: inline-flex;
  align-items: center;
  position: relative;
  height: 16px;
}

.cmx-radio input[type=radio]+label::before {
  content: " ";
  display: inline-block;
  width: 16px;
  height: 16px;
  box-sizing: border-box;
  border-width: 1px;
  border-color: #B0B0B0;
  border-style: solid;
  border-radius: 8px;
  margin-right: 0.375rem;
}

.cmx-radio input[type=radio]:hover+label::before,.cmx-radio input[type=radio]:focus+label::before {
  background-color: #e8ebef;
}

.cmx-radio input[type=radio]:checked+label::after {
  content: ' ';
  top: 4px;
  left: 4px;
  position: absolute;
  width: 8px;
  height: 8px;
  background-color: #3FA9F5;
  border-radius: 4px;
}

.cmx-radio input[type=radio]:checked.error+label::after {
  background-color: #b71c1c;
}

.cmx-radio input[type=radio].error+label::before {
  border-color: #EE3D42;
}

.cmx-radio input[type=radio][disabled]+label::before {
  background-color: #D6D6D6;
}

.cmx-radio[dir="rtl"] input[type=radio] {
  margin-left: 0.375rem;
  margin-right: 0;
}

.cmx-radio[dir="rtl"] input[type=radio]+label::before {
  margin-left: 0.375rem;
  margin-right: 0;
}

.cmx-radio[dir="rtl"] input[type=radio]:checked+label::after {
  right: 4px;
  left: auto;
}

