@font-face {
  font-family: "Roboto";
  font-family: "Roboto-Thin";
  font-weight: 100;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Light.ttf");
  font-weight: 300;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Regular.ttf");
  font-weight: 400;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Italic.ttf");
  font-weight: 400;
  font-style: italic;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Medium.ttf");
  font-weight: 500;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Bold.ttf");
  font-weight: 700;
}

@font-face {
  font-family: "Roboto";
  src: url("https://www.cemexgo.com/cdn/fonts/Roboto-Black.ttf");
  font-weight: 800;
}

.cmx-tooltip {
  border: 1px solid #333;
  border-radius: 3px;
  color: #E8EBEF;
  font-size: .75rem;
  background: #333;
  padding: 0.5rem 1rem;
  position: absolute;
  font-weight: 500;
  position: unset;
  color: #E8EBEF;
  padding: 8px 16px;
  font-family: "Roboto",sans-serif;
  font-weight: 500;
  max-width: 500px;
  box-sizing: border-box;
  -moz-box-sizing: border-box;
  -webkit-box-sizing: border-box;
  display: inline-block;
  min-height: 32px;
  width: auto;
}

.cmx-tooltip::after {
  content: "";
  position: absolute;
  width: 0;
  height: 0;
  margin-left: -10px;
  bottom: -13px;
  left: 50%;
  box-sizing: border-box;
  border: 6px solid black;
  border-color: transparent transparent #333 #333;
  transform-origin: 0 0;
  transform: rotate(-45deg);
}

.cmx-tooltip--up::after {
  border-color: #333 #333 transparent transparent;
  bottom: auto;
  top: 0;
}

.cmx-tooltip--left::after {
  border-color: #333 transparent transparent #333;
  bottom: auto;
  left: 1px;
  top: 50%;
}

.cmx-tooltip--right::after {
  border-color: transparent #333 #333 transparent;
  bottom: auto;
  right: -4px;
  top: 50%;
  left: auto;
}

.cmx-tooltip::after {
  bottom: -12px;
}

.cmx-tooltip--up::after {
  border-color: #333 #333 transparent transparent;
  bottom: auto;
  top: 0;
  left: calc(50% - 1.5px);
}

.cmx-tooltip--left::after {
  border-color: #333 transparent transparent #333;
  bottom: auto;
  left: 2px;
  top: calc(50% - 1.5px);
}

.cmx-tooltip--right::after {
  border-color: transparent #333 #333 transparent;
  bottom: auto;
  right: -3px;
  top: calc(50% - 1.5px);
  left: auto;
}

